muclient.script:
t = {
    ["<code>"] = "<pre>",
    ["<CODE>"] = "<pre>",
    ["</code>"] = "</pre><br>",
    ["</CODE>"] = "</pre><br>",
    ["<tt>"] = "<pre>",
    ["<TT>"] = "<pre>",
    ["</tt>"] = "</pre><br>",
    ["</TT>"] = "</pre><br>",
    }

function f (s)
  return t [s] or s
end -- f

function OnPluginPacketReceived (s)
  return (string.gsub (s, "</?%a+>", f))
end -- function OnPluginPacketReceived
